package info.nukoneko.android.photoeffect.controller.common.view;import android.content.Context;import android.widget.Button;import info.nukoneko.android.photoeffect.sys.enm.NKFilterType;/** * Created by Atsumi3 on 2016/11/12. */public final class FilterButton extends Button {    public interface OnClickFilterButtonListener {        void onClickFilterButton(FilterButton filterButton);    }    private final NKFilterType NKFilterType;    public FilterButton(Context context, NKFilterType NKFilterType, FilterButton.OnClickFilterButtonListener listener) {        super(context);        this.NKFilterType = NKFilterType;        this.setText(NKFilterType.getFilterName());        this.setOnClickListener(view -> listener.onClickFilterButton(this));    }    public NKFilterType getNKFilterType() {        return NKFilterType;    }}