package info.nukoneko.android.photoeffect.sys;import android.app.Application;import android.graphics.Point;import android.view.Display;import android.view.WindowManager;/** * Created by Atsumi3 on 2016/11/12. */public final class NKAppController extends Application {    private static NKAppController mInstance;    private Point windowSize = new Point();    @Override    public void onCreate() {        super.onCreate();        mInstance = this;        WindowManager wm = (WindowManager) getSystemService(WINDOW_SERVICE);        Display display = wm.getDefaultDisplay();        display.getSize(this.windowSize);    }    public Point getWindowSize() {        return windowSize;    }    public static synchronized NKAppController getApp() {        return mInstance;    }}